mapper.update:
update shopping_cart
set computer_id = #{computerId,jdbcType=INTEGER},
  computer_count = #{computerCount,jdbcType=INTEGER},
  shopping_cart_state = #{shoppingCartState,jdbcType=INTEGER},
  user_id = #{userId,jdbcType=INTEGER}
where shopping_id = #{shoppingId,jdbcType=INTEGER}

mapper.select:
select shopping_id, computer_id, computer_count, shopping_cart_state, user_id
from shopping_cart
where shopping_id = #{shoppingId,jdbcType=INTEGER}
